Button.ClientEvent:
var data = view.get("#dsCondition4").getData("#");
		with(view.get("#dsTbih03")){
			set("parameter", data);
			flushAsync();
		}

DataPilot.ClientEvent:
var datas = view.get("#dsTbih03.data");
switch(arg.code){
	case "+":{
		datas.insert();
		arg.processDefault=false;
		break;
	}
	case "-":{
		
        var dgTbih03 = view.get("#dgTbih03");
	    var updateAction = view.get("#updateAction");
		var selection = dgTbih03.get('selection');//获取所有选中的数据
		if(selection.length>0){
			dorado.MessageBox.confirm("您真的想删除勾选数据吗?",function(){
				selection.each(function(entity){
					entity.remove();
				});
				updateAction.execute();
	        });
		}else{
			view.get("#msg3").set("text","请先勾选需要删除的数据");
		}
        arg.processDefault=false;
        break;
	}
}

ToolBarButton.ClientEvent:
var dgTbih03 = view.get("#dgTbih03");
var selection = dgTbih03.get('selection');//获取所有选中的数据
if(selection.length>0){
	var ajaxActionIh03 = view.get("#ajaxActionIh03");
	ajaxActionIh03.set("parameter",{
		'ListIh03' : selection,
		'status' : "01"
	});
	ajaxActionIh03.execute(function() {
		view.get("#msg").set("text","启用成功");
		var data = view.get("#dsCondition4").getData("#");
		with(view.get("#dsTbih03")){
			set("parameter", data);
			flushAsync();
		}
	});
}else{
	view.get("#msg").set("text","请先勾选需要启用的数据");
}

var dgTbih03 = view.get("#dgTbih03");
var selection = dgTbih03.get('selection');//获取所有选中的数据
if(selection.length>0){
	var ajaxActionIh03 = view.get("#ajaxActionIh03");
	ajaxActionIh03.set("parameter",{
		'ListIh03' : selection,
		'status' : "10"
	});
	ajaxActionIh03.execute(function() {
		view.get("#msg3").set("text","停用成功");
		view.get("#dgTbih03").unselectAll();
	});
}else{
	view.get("#msg3").set("text","请先勾选需要停用的数据");
}

