DataPilot.ClientEvent:
var datas = view.get("#dataSetTbih16a.data");
var action = view.get("#actionUpOrDelTbih16a");
switch(arg.code){
	case "+":{
		//得到主表的数据集对象
        var ih16Entity = this.get("#dataSetTbih16").getData("#");    
        //根据主表的数据集得到其中的那个reference对应的数据集
        var dsTbih16a = this.get("#dataSetTbih16a"); 
        //根据得到的数据集向其中插入一条对象，注意的是：这里的deptId不是数据库中的字段，而是实体类的属性
        dsTbih16a.insert({ih16Id:ih16Entity.get("ih16Id"),deliveryNo:ih16Entity.get("deliveryNo")});	
		if(ih16Entity){
			view.get("#dataSetOrderItem").set("parameter",{custNo:ih16Entity.get("custNo"),deliveryNo:ih16Entity.get("deliveryNo")});
			view.get("#dataSetOrderItem").flush();
			view.get("#dataSetTbih30").set("parameter",{custNo:ih16Entity.get("custNo")});
			view.get("#dataSetTbih30").flush();
		}
		view.get("#dialogTbih16a").show();
		arg.processDefault=false;
		break;
	}
	case "-":{
		dorado.MessageBox.confirm("您真的想删除当前数据吗?",function(){
			var dgTbih16a = view.get("#dgTbih16a");
			var selection = dgTbih16a.get('selection');//获取所有选中的数据
			selection.each(function(entity){
				entity.remove();
			});
			action.execute();
		});
		arg.processDefault=false;
		break;
	}
}

