	view.get("#dataSetTbih16a").flush();
}

DataPilot.ClientEvent:
var datas = view.get("#dataSetTbih16.data");
var action = view.get("#actionUpdateTbih16");
switch(arg.code){
	case "+":{
		var entityNew=datas.insert();
		entityNew.set("deliveryDate",new Date().formatDate ("Ymd H:m:s"));
	    with(view.get("#dataSetTbih16b")){
//			set("parameter", data);
			flush();
		}
		view.get("#dialogTbih16").show();
		arg.processDefault=false;
		break;
	}
	case "-":{
		dorado.MessageBox.confirm("您真的想删除当前数据吗?",function(){
			datas.remove();
			action.execute(function(returnValue){
			    var flag = returnValue["flag"];
			    var msg = returnValue["msg"];
				if(flag){
					dorado.widget.NotifyTipManager.notify("删除成功");
					view.get("#dataSetTbih16").flush();
					view.get("#dataSetTbih16a").flush();
				}else{
					alert(msg);
				}
			});
		});
		arg.processDefault=false;
		break;
	}
}

