var dataCurrent = view.get("#dataSetTbih16").getData("#");
var ajaxActionTbih16Submit = view.get("#ajaxActionTbih16Submit");
ajaxActionTbih16Submit.set("parameter",{
	'Ih16' : dataCurrent,
	'status' : "01"
});
ajaxActionTbih16Submit.execute(function(returnValue) {
	var flag = returnValue["flag"];
    var msg = returnValue["msg"];
	if(flag){
		dorado.widget.NotifyTipManager.notify("提交成功");
		var data = view.get("#formCondition.entity");
		with (view.get("#dataSetTbih16")){
			set("parameter", data);
			flushAsync();
		}
	}else{
		alert(msg);
	}
	
});

