	view.get("#dataSetTbih16a").insert(entityIh16b.toJSON());
}

dorado.MessageBox.confirm("您真的要保存吗?",function(){
	actionTbih16.execute(function(returnValue){
		var flag = returnValue["flag"];
    	var msg = returnValue["msg"];
		if(flag){
			view.get("#msg").set("text","保存成功");
			if(view.get("#dataSetTbih16").getData("#")){
				var entity=view.get("#dataSetTbih16").getData("#");
				view.get("#dataSetTbih16a").set("parameter",{ih16Id:entity.get("ih16Id"),deliveryNo:entity.get("deliveryNo")});
				view.get("#dataSetTbih16a").flush();
			}
			dialog.hide();
		}else{
			view.get("#msg").set("text",msg);
		}
		
	});
})

