Button.ClientEvent:
var dialog=view.get("#dialogTbih16a");

var entity=view.get("#dataSetTbih16").getData("#");

var dgTbih30 = view.get("#dataGridTbih30");
var dgOrderItem = view.get("#dataGridOrderItem");
var selectionOrderItem = dgOrderItem.get('selection');//获取所有选中的数据
var selectionTbih30 = dgTbih30.get('selection');
var sfxzztxc="";
if(selectionOrderItem.length>0 && selectionTbih30.length==0){
	sfxzztxc="Y";
}
if(selectionTbih30.length>0){
	sfxzztxc="N";
}
if(sfxzztxc){
	dorado.MessageBox.confirm("您真的要保存吗?",function(){
		var actionTbih16a=view.get("#actionUpdateTbih16a");
		actionTbih16a.set("parameter",
		{	dataOrderItems:selectionOrderItem,
			ListIh30:selectionTbih30,
			sfxzztxc:sfxzztxc,
			deliveryNo:entity.get("deliveryNo"),
			ih16Id:entity.get("ih16Id")}
		).execute(function(){
			var entity=view.get("#dataSetTbih16").getData("#");
			view.get("#dataSetTbih16a").set("parameter",{ih16Id:entity.get("ih16Id"),deliveryNo:entity.get("deliveryNo")});
			view.get("#dataSetTbih16a").flush();
			dialog.hide();
		});
	})
}else{
	alert("请先选择要保存的提单明细信息");
}

var data=view.get("#dataSetTbih16a").getData();
var dialog=view.get("#dialogTbih16a");
dorado.MessageBox.confirm("您真的要取消当前操作吗？",function(){
	data.cancel();
	dialog.hide();
});

