ImportExcelAction.ClientEvent:
var entity=view.get("#dataSetTbih21").getData("#");
var ajaxActionContNo = view.get("#ajaxActionContNo");
ajaxActionContNo.set("parameter",{
	'contNo' : entity.get("contNo"),
	'ih21Id' : entity.get("ih21Id"),
	'custNo' : entity.get("custNo"),
	'custName' : entity.get("custName")
});
ajaxActionContNo.execute(function() {
	view.get("#dataSetTbih22").set("parameter",{contNo:entity.get("contNo")});
	view.get("#dataSetTbih22").flushAsync();
});

Button.ClientEvent:
var data = view.get("#formCondition.entity");
var tabControl1 = view.get("#tabControl1");
var curtab = tabControl1.get("currentTab");
if("tab1"==curtab.get("name")){
	with (view.get("#dataSetTbih21")){
		set("parameter", data);
		flushAsync();
	}
}

var dialog=view.get("#dialogTbih21");
var actionTbih21=view.get("#actionUpdateTbih21");
var entity=view.get("#dataSetTbih21").getData("#");
dorado.MessageBox.confirm("您真的要保存吗?",function(){
	actionTbih21.set("parameter",{ih21Id:entity.get("ih21Id"),contNo:entity.get("contNo")}).execute(function(){
		dialog.hide();
	});
})

