DataPilot.ClientEvent:
var datas = view.get("#dataSetTbih21.data");
var action = view.get("#actionUpdateTbih21");
switch(arg.code){
	case "+":{
		var ih21NewEntity=datas.insert();
		ih21NewEntity.set("year", (new Date()).formatDate ("Ym"));
		ih21NewEntity.set("mdate",(new Date()).formatDate ("Ymd"));
		ih21NewEntity.set("kljs", "1");
		ih21NewEntity.set("settleCompNo", "0");
		view.get("#dialogTbih21").show();
		arg.processDefault=false;
		break;
	}
	case "-":{
		dorado.MessageBox.confirm("您真的想删除勾选数据吗?",function(){
            var dgTbih21 = view.get("#dgTbih21");
			var actionUpdateTbih21 = view.get("#actionUpdateTbih21");
			var selection = dgTbih21.get('selection');//获取所有选中的数据
			selection.each(function(entity){
				entity.remove();
			});
			actionUpdateTbih21.execute();
        });
        arg.processDefault=false;
        break;
	}
}

ToolBarButton.ClientEvent:
var data=view.get("#dataSetTbih21.data");
if(data.current){
	var entity=view.get("#dataSetTbih21").getData("#");
	view.get("#dialogTbih21").show();
}else{
	dorado.MessageBox.alert("当前表没有信息可供编辑!");
}

var dsTbih21 = view.get("#dataSetTbih21").getData("#");
var contNo = dsTbih21.get("contNo");
var compId = dsTbih21.get("compId");
var path="com.jghz.sh.view.shjv31.d?contNo='"+contNo+"'&compId='"+compId+"'";

//tabControlWorkarea d7框架固定写法
var tabcontrol=window.parent.$id('tabControlWorkarea').objects[0];
var iframeTab = new parent.dorado.widget.tab.IFrameTab({
path:path,
icon:"url(>skin>common/icons.gif) -0px -100px",
caption:"运输费价格定义"
});
iframeTab.set("closeable",true);
tabcontrol.addTab(iframeTab);
tabcontrol.set("currentTab",iframeTab);

