DataPilot.ClientEvent:
 var code = arg.code;
   
   switch(code){
     case "+":
	 	//得到主表的数据集对象
	     var ih21Entity = this.get("#dataSetTbih21").getData("#");
	     
	    
	     //根据主表的数据集得到其中的那个reference对应的数据集
	     var dsTbih22 = this.get("#dataSetTbih22");
	     
	     //根据得到的数据集向其中插入一条对象，注意的是：这里的deptId不是数据库中的字段，而是实体类的属性
	     dsTbih22.insert({ih21Id:ih21Entity.get("ih21Id"),
		 			      contNo:ih21Entity.get("contNo"),
						  prodType:ih21Entity.get("prodType"),
						  prodTypeName:ih21Entity.get("prodTypeName"),
						  productNo:ih21Entity.get("productNo"),
						  productName:ih21Entity.get("productName"),
						  orderCompNo:ih21Entity.get("custNo"),
						  orderCompName:ih21Entity.get("custName")});
	     view.get("#dialogTbih22").show();
	     //防止系统自动的添加一行
	     arg.processDefault = false;
	     break;
	 
     case "-":{
		dorado.MessageBox.confirm("您真的想删除勾选数据吗?",function(){
            var dgTbih22 = view.get("#dgTbih22");
			var actionUpdateTbih22 = view.get("#actionUpdateTbih22");
			var selection = dgTbih22.get('selection');//获取所有选中的数据
			selection.each(function(entity){
				entity.remove();
			});
			actionUpdateTbih22.execute();
        });
        arg.processDefault=false;
        break;
	}
   }

ToolBarButton.ClientEvent:
var data=view.get("#dataSetTbih22.data");
if(data.current){
	view.get("#dialogTbih22").show();
}else{
	dorado.MessageBox.alert("当前表没有信息可供编辑!");
}

var dgTbih22 = view.get("#dgTbih22");
var entity=view.get("#dataSetTbih21").getData("#");
var selection = dgTbih22.get('selection');//获取所有选中的数据
if(selection.length>0){
	var aActionLadingNo = view.get("#ajaxActionLadingNo");
	aActionLadingNo.set("parameter",{
		'ListIh22' : selection,
		'Ih21' : entity
	});
	aActionLadingNo.execute(function() {
		dorado.widget.NotifyTipManager.notify("生成成功");	
		view.get("#dataSetTbih22").set("parameter",{contNo:entity.get("contNo")});
		view.get("#dataSetTbih22").flushAsync();
	});
}else{
	dorado.widget.NotifyTipManager.notify("请先勾选需要生成的数据");
}

var importExcelAction=view.get("#importExcelAction");
importExcelAction.set("excelModelId","IH0003");
importExcelAction.execute(function(arg){
	dorado.MessageBox.alert("成功解析导入[" + arg + "]数据！");
});

CustomDropDown.ClientEvent:
var data=view.id("dataSetTbih21").getData("#");
if(!data){
	dorado.MessageBox.alert("请先选中一个角色!");
	arg.processDefault=false;
	return;
}
var dialog=view.id("$dialogCustNo");
	dialog.$show({
		width:600,
		height:500,
		callback:function(selectArray){
			dialog.hide();
			data.set("custNo",selectArray["custNo"]);
			data.set("custName",selectArray["custName"]);
		}
	});

