DataSet.ClientEvent:
var contNo=${request.getParameter("contNo")}+"";
var compId=${request.getParameter("compId")}+"";
view.get("#dataSetTbih21").set("parameter",{contNo:contNo,compId:compId});
view.get("#dataSetTbih21").flush();
var dataTbih21 = view.get("#dataSetTbih21").getData("#");
view.get("#dataSetTbih21b").set("parameter",{contNo:contNo,compId:compId,Ih21:dataTbih21});
view.get("#dataSetTbih21b").flush();
view.get("#dataSetTbih03e").set("parameter",{contNo:contNo,compId:compId});
view.get("#dataSetTbih03e").flush();
view.get("#dataSetTbih03f").set("parameter",{contNo:contNo,compId:compId});
view.get("#dataSetTbih03f").flush();

DataPilot.ClientEvent:
 var code = arg.code;
   
   switch(code){
     case "+":
     //得到主表的数据集对象
     var ih21Entity = this.get("#dataSetTbih21").getData("#");
     //根据主表的数据集得到其中的那个reference对应的数据集
     var dsTbih21b = this.get("#dataSetTbih21b");  
     dsTbih21b.insert({ih21Id:ih21Entity.get("ih21Id"),
	 				   contNo:ih21Entity.get("contNo"),
					   custNo:ih21Entity.get("custNo"),
					   custName:ih21Entity.get("custName")}); 
     //防止系统自动的添加一行
     arg.processDefault = false;
     break;
	 
	 case "-":{
	 	var dgTbih21b = view.get("#dataGridTbih21b");
		var selection = dgTbih21b.get('selection');//获取所有选中的数据
		if(selection.length>0){
			dorado.MessageBox.confirm("您真的想删除勾选数据吗?",function(){
				var ajaxActionIh21b = view.get("#ajaxActionIh21b");
				ajaxActionIh21b.set("parameter",{
					'ListIh21b' : selection,
					'deleteFlag' : "Y"
				});
				ajaxActionIh21b.execute(function() {
					dorado.widget.NotifyTipManager.notify("删除成功");
					var dataTbih21 = view.get("#dataSetTbih21").getData("#");
					var contNo=dataTbih21.get("contNo");
					var compId=dataTbih21.get("compId");
					view.get("#dataSetTbih21b").set("parameter",{contNo:contNo,compId:compId});
					view.get("#dataSetTbih21b").flushAsync();
				});
			});
		}else{
			dorado.widget.NotifyTipManager.notify("请先勾选需要删除的数据");
		}
        arg.processDefault=false;
        break;
    }
	 
   }

ToolBarButton.ClientEvent:
var dgTbih21b = view.get("#dataGridTbih21b");
var datasTbih03a = view.get("#dataSetTbih03a.data");
var datasTbih03c = view.get("#dataSetTbih03c.data");
var selection = dgTbih21b.get('selection');//获取所有选中的数据
if(selection.length>0){
	var ajaxActionIh21b = view.get("#ajaxActionIh21b");
	ajaxActionIh21b.set("parameter",{
		'ListIh21b' : selection,
		'ListIh03a' : datasTbih03a,
		'ListIh03c' : datasTbih03c,
		'status' : "01"
	});
	ajaxActionIh21b.execute(function() {
		dorado.widget.NotifyTipManager.notify("保存成功");
		var dataTbih21 = view.get("#dataSetTbih21").getData("#");
		var contNo=dataTbih21.get("contNo");
		var compId=dataTbih21.get("compId");
		view.get("#dataSetTbih21b").set("parameter",{contNo:contNo,compId:compId});
		view.get("#dataSetTbih21b").flushAsync();
		view.get("#dataSetTbih03a").set("parameter",{contNo:contNo,compId:compId});
		view.get("#dataSetTbih03a").flushAsync();
		view.get("#dataSetTbih03c").set("parameter",{contNo:contNo,compId:compId});
		view.get("#dataSetTbih03c").flushAsync();
	});
}else{
	dorado.widget.NotifyTipManager.notify("请先勾选需要保存的数据");
}

var dgTbih21b = view.get("#dataGridTbih21b");
var datasTbih03a = view.get("#dataSetTbih03a.data");
var datasTbih03c = view.get("#dataSetTbih03c.data");
var selection = dgTbih21b.get('selection');//获取所有选中的数据
if(selection.length>0){
	var aActionCorC = view.get("#ajaxActionCommitOrCancle");
	aActionCorC.set("parameter",{
		'ListIh21b' : selection,
		'ListIh03a' : datasTbih03a,
		'ListIh03c' : datasTbih03c,
		'status' : "01"
	});
	aActionCorC.execute(function() {
		dorado.widget.NotifyTipManager.notify("启用成功");
		var dataTbih21 = view.get("#dataSetTbih21").getData("#");
		var contNo=dataTbih21.get("contNo");
		var compId=dataTbih21.get("compId");
		view.get("#dataSetTbih21b").set("parameter",{contNo:contNo,compId:compId});
		view.get("#dataSetTbih21b").flushAsync();
		view.get("#dataSetTbih03a").set("parameter",{contNo:contNo,compId:compId});
		view.get("#dataSetTbih03a").flushAsync();
		view.get("#dataSetTbih03c").set("parameter",{contNo:contNo,compId:compId});
		view.get("#dataSetTbih03c").flushAsync();
	});
}else{
	dorado.widget.NotifyTipManager.notify("请先勾选需要启用的数据");
}

