DataPilot.ClientEvent:
var datas = view.get("#dataSetTbih03e.data");
switch(arg.code){
	case "+":{
		datas.insert();
		arg.processDefault=false;
		break;
	}
	case "-":{
        var dgTbih03e = view.get("#dgTbih03e");
	    var updateActione = view.get("#updateActione");
		var selection = dgTbih03e.get('selection');//获取所有选中的数据
		if(selection.length>0){
			dorado.MessageBox.confirm("您真的想删除勾选数据吗?",function(){
				selection.each(function(entity){
					entity.remove();
				});
				updateActione.execute();
	        });
		}else{
			view.get("#msg6").set("text","请先勾选需要删除的数据");
		}
        arg.processDefault=false;
        break;
	}
}

ToolBarButton.ClientEvent:
var dgTbih03e = view.get("#dgTbih03e");
var selection = dgTbih03e.get('selection');//获取所有选中的数据
if(selection.length>0){
	var ajaxActionIh03e = view.get("#ajaxActionIh03e");
	ajaxActionIh03e.set("parameter",{
		'ListIh03e' : selection,
		'status' : "01"
	});
	ajaxActionIh03e.execute(function() {
		view.get("#msg6").set("text","启用成功");
		var data = view.get("#dsCondition6").getData("#");
		with(view.get("#dsTbih03e")){
			set("parameter", data);
			flushAsync();
		}
	});
}else{
	view.get("#msg6").set("text","请先勾选需要启用的数据");
}

var dgTbih03e = view.get("#dgTbih03e");
var selection = dgTbih03e.get('selection');//获取所有选中的数据
if(selection.length>0){
	var ajaxActionIh03e = view.get("#ajaxActionIh03e");
	ajaxActionIh03e.set("parameter",{
		'ListIh03e' : selection,
		'status' : "10"
	});
	ajaxActionIh03e.execute(function() {
		view.get("#msg6").set("text","停用成功");
		var data = view.get("#dsCondition6").getData("#");
		with(view.get("#dsTbih03e")){
			set("parameter", data);
			flushAsync();
		}
	});
}else{
	view.get("#msg6").set("text","请先勾选需要停用的数据");
}

