CustomDropDown.ClientEvent:
var data=view.id("dataSetTbih21b").getData("#");
if(!data){
	dorado.MessageBox.alert("请先选中一个数据!");
	arg.processDefault=false;
	return;
}
var dialog=view.id("$dialogProdNo");
	dialog.$show({
		width:600,
		height:500,
		callback:function(selectArray){
			dialog.hide();
			data.set("prodType",selectArray["typeId"]);
			data.set("prodTypeName",selectArray["typeName"]);
			data.set("productNo",selectArray["id"]);
			data.set("productName",selectArray["name"]);
		}
	});
