Button.ClientEvent:
var data = view.get("#formConditioin.entity");
with (view.get("#dataSetTbih40")){
	set("parameter", data);
	view.get("#dataSetTbih40").flush();
}
if(view.get("#dataSetTbih40").getData("#")){
	var entity=view.get("#dataSetTbih40").getData("#");
	view.get("#dataSetTbih40a").set("parameter",{ih40Id:entity.get("ih40Id"),settleNo:entity.get("settleNo")});
	view.get("#dataSetTbih40a").flush();
}

DataPilot.ClientEvent:
var datas = view.get("#dsTbih50.data");
switch(arg.code){
	case "+":{
		var entityNew=datas.insert();
		entityNew.set("jkrq",new Date().formatDate ("Ymd"));
		view.get("#dialogIh50").show();
		arg.processDefault=false;
		break;
	}
}

Button.ClientEvent:
var data=view.get("#dsTbih50.data");
if(data.current){
	view.get("#dialogIh50").show();
}else{
	dorado.MessageBox.alert("当前表没有信息可供编辑!");
}

var dgTbih50 = view.get("#dgTbih50");

var selection = dgTbih50.get('selection');//获取所有选中的数据
if(selection.length>0){
	var ajaxActionIh50Stu = view.get("#ajaxActionIh50Stu");
	ajaxActionIh50Stu.set("parameter",{
		'ListIh50' : selection,
		'status' : "01"
	});
	ajaxActionIh50Stu.execute(function(returnValue) {
		var flag = returnValue["flag"];
    	var msg = returnValue["msg"];
		if(flag){
			view.get("#msg").set("text","审核成功");
			var data = view.get("#formConditioin.entity");
			with (view.get("#dsTbih50")){
				set("parameter", data);
				view.get("#dsTbih50").flush();
			}
		}else{
			alert(msg);
		}
	});
}else{
	view.get("#msg").set("text","请先勾选需要审核的数据");
}

var dgTbih50 = view.get("#dgTbih50");

