ToolBarButton.ClientEvent:
var dgTbih40 = view.get("#dataGridTbih40");
var selection = dgTbih40.get('selection');//获取所有选中的数据
if(selection.length>0){
	var ajaxActionIh40 = view.get("#ajaxActionIh40");
	ajaxActionIh40.set("parameter",{
		'ListIh40' : selection,
		'status' : "10"
	});
	ajaxActionIh40.execute(function() {
		dorado.widget.NotifyTipManager.notify("确认成功");
	});
}else{
	alert("请先勾选需要确认的数据");
}

CustomDropDown.ClientEvent:
var data=view.id("dataSetTbih40").getData("#");
if(!data){
	dorado.MessageBox.alert("请先选中一个角色!");
	arg.processDefault=false;
	return;
}
var dialog=view.id("$dialogCustNo");
	dialog.$show({
		width:600,
		height:500,
		callback:function(selectArray){
			dialog.hide();
			data.set("custNo",selectArray["custNo"]);
			data.set("custName",selectArray["custName"]);
		}
	});

var data=view.id("dsTbih50").getData("#");
