Button.ClientEvent:
var dialog=view.get("#dialogIh50");
var action=view.get("#updateActionTbih50");
var entity = this.get("#dsTbih50").getData("#");
var custNo = entity.get("custNo");
dorado.MessageBox.confirm("您真的要保存吗?",function(){
	 action.set("parameter",{
			       "custNo":custNo
		     });
	action.execute(function(){
		var data = view.get("#dsCondition1").getData("#");
		with(view.get("#dsTbih50")){
			set("parameter", data);
			flushAsync();
		}
		dialog.hide();
	});
})

var data=view.get("#dsTbih50").getData();
var dialog=view.get("#dialogIh50");
dorado.MessageBox.confirm("您真的要取消当前操作吗？",function(){
	data.cancel();
	dialog.hide();
});
