self.move=function(entities){
	var ds=view.id("dsTbih30left");
	var dataLeft=ds.getData("#");
	if(dataLeft){
		var targetDataRight=view.id("dsTbih30right").getData();
		var insertAble=true;
		targetDataRight.each(function(dataRight){
			if(dataRight.get("labelNo")==dataLeft.get("labelNo")){
				insertAble=false;
				return false;
			}
		});
		targetDataRight.insert(dataLeft.toJSON());
			dataLeft.remove();
	}else{
		dorado.MessageBox.alert("当前没有选中的需要货权转授的信息！")
	}	
}

