var action = view.get("#updateActionDelete");
var dgTbih60a = view.get("#dgTbih60a");
var selection = dgTbih60a.get('selection');//获取所有选中的数据
if(selection.length>0){
	dorado.MessageBox.confirm("您真的想删除勾选数据吗?",function(){
		selection.each(function(entity){
			entity.remove();
		});
		action.execute();
	});
}else{
	view.get("#msg2").set("text","请先勾选需要删除的数据");
}

var TextWareQwCode = view.id("TextWareQwCode");
var dgTbih60a = view.get("#dgTbih60a");
var selection = dgTbih60a.get('selection');//获取所有选中的数据
if(selection.length>0){
	selection.each(function(entity){
		entity.set("wareQwCode",TextWareQwCode.get("value"));
	});
}else{
	view.get("#msg2").set("text","请先勾选需要设置库位的数据");
}

var data = view.get("#formCondition.entity");
with(view.get("#dsTbih30left")){
	set("parameter", data);
	flushAsync();
}

var dgLeft = view.get("#dgTbih30left");
var selectionLeft = dgLeft.get("selection");
if(selectionLeft.length>0){
	selectionLeft.each(function(dataLeft){
		var targetDataRight=view.id("dsTbih30right").getData();
		var insertAble=true;
		targetDataRight.each(function(dataRight){
			if(dataRight.get("ih30Id")==dataLeft.get("ih30Id")){
				insertAble=false;
				return false;
			}
		});
		targetDataRight.insert(dataLeft.toJSON());
		dataLeft.remove();
	});
}else{
	dorado.MessageBox.alert("当前没有选中的需要配货的信息！")
}

var dgRight = view.get("#dgTbih30rigt");
var selectionRight = dgRight.get("selection");
if(selectionRight.length>0){
	selectionRight.each(function(dataRight){
		var targetDataLeft=view.id("dsTbih30left").getData();
		var insertAble=true;
		targetDataLeft.each(function(dataLeft){
			if(dataRight.get("ih30Id")==dataLeft.get("ih30Id")){
				insertAble=false;
				return false;
			}
		});
		targetDataLeft.insert(dataRight.toJSON());
		dataRight.remove();
	});

	dgRight.refresh();
	
}else{
	dorado.MessageBox.alert("当前没有选中的需要左移的信息！")
}

var entity = view.get("#formZS").get("entity");
var dsTbih30right = view.get("#dsTbih30right").getData();
if(dsTbih30right.entityCount>0){
	if(entity.get("nhz")==null){
		view.get("#msg").set("text","消息提示： 新货主不能为空！");
		return false;
	}
	dorado.MessageBox.confirm("是否确认转授有勾选的数据？",function(){
		var action = view.get("#updateActionZS");
		  action.set("parameter",entity);
		action.execute(function(){
			view.get("#msg").set("text","消息提示： 审核成功！");
		});
	});
}else{
	view.get("#msg").set("text","消息提示： 请先勾选需要转授的数据！");
}

