Button.ClientEvent:
var entity = view.get("#formCondition").get("entity");

view.get("#dataSetTbwd01").set("parameter", entity);
view.get("#dataSetTbwd01").flushAsync(function(entityList){
    if (entityList.isEmpty()) {
        view.get("#msg").set("text", "消息提示： 查询无数据");
    }
    else {
        view.get("#msg").set("text", "消息提示： 查询成功");
    }
});

DataPilot.ClientEvent:
var datas = view.get("#dataSetTbwd01.data");
var action = view.get("#actionUpdate");
switch (arg.code) {
    case "+":{
        datas.insert();
        var dataList = view.get("#dataSetTbwd01").getData(); //取得原始数据列表
        var selectedItems = []; //定义一个数组
        dataList.each(function(entity){
            if (entity.state == dorado.Entity.STATE_NEW) {
                selectedItems.push(entity);
                view.get("#dataSetTbwd01.data").setCurrent(entity);
            }
        });
        view.set(("#dgTbwd01.selection"), selectedItems);
        view.get("#dialogTbwd01").show();
        view.get("#ccfImgA").set("image", null);
        view.get("#clImgA").set("image", null);
        view.get("#roadImgA").set("image", null);
        view.get("#bcImgA").set("image", null);
        view.get("#azgImgA").set("image", null);
        view.get("#idImgA").set("image", null);
        view.get("#id2ImgA").set("image", null);
        
        // 添加时给对话框初始化显示状态
        view.get("#ccfUpload").set("caption", "开始上传");
        view.get("#bt1Msg").set("caption", "等待上传");
        view.get("#bt1Msg").set("icon", "  url(>skin>common/icons.gif) -20px -220px");
        
        view.get("#clUpload").set("caption", "开始上传");
        view.get("#bt2Msg").set("caption", "等待上传");
        view.get("#bt2Msg").set("icon", "  url(>skin>common/icons.gif) -20px -220px");
        
        view.get("#btUpload").set("caption", "开始上传");
        view.get("#btMsg").set("caption", "等待上传");
        view.get("#btMsg").set("icon", "  url(>skin>common/icons.gif) -20px -220px");
        
        view.get("#bcUpload").set("caption", "开始上传");
        view.get("#bt4Msg").set("caption", "等待上传");
        view.get("#bt4Msg").set("icon", "  url(>skin>common/icons.gif) -20px -220px");
        
        view.get("#azgUpload").set("caption", "开始上传");
        view.get("#bt5Msg").set("caption", "等待上传");
        view.get("#bt5Msg").set("icon", "  url(>skin>common/icons.gif) -20px -220px");
        
        view.get("#idUpload").set("caption", "开始上传");
        view.get("#bt6Msg").set("caption", "等待上传");
        view.get("#bt6Msg").set("icon", "  url(>skin>common/icons.gif) -20px -220px");
        
        view.get("#id2Upload").set("caption", "开始上传");
        view.get("#bt7Msg").set("caption", "等待上传");
        view.get("#bt7Msg").set("icon", "  url(>skin>common/icons.gif) -20px -220px");
        
        
        
        arg.processDefault = false;
        break;
    }
    case "-":{
        var dataGrid = view.get("#dgTbwd01");//获取数据表格控件
        var selEntitys = dataGrid.get("selection");//获取所有被勾选的实体集合entityList
        if (selEntitys.length > 0) {
            var flag = false;
            dorado.MessageBox.confirm("您真的想删除当前数据吗?", function(){
                selEntitys.each(function(select){
                    if (select.get("state") == 1) {
                        dorado.MessageBox.alert("不能删除已审核数据");
                    }
                    else {
                        select.remove();
                        flag = true;
                    }
                })
                if (flag) {
                    action.execute(function(){
                        view.get("#actionUpdate").set("successMessage", "删除成功!");
                    });
                }
                
            });
        }
        if (selEntitys.length < 1) {
            view.get("#msg").set("text", "消息提示：请至少勾选一条数据！");
            
        }
        arg.processDefault = false;
        break;
    }
}

