Button.ClientEvent:
var entity = view.get("#afCondition").get("entity");
view.get("#dsWayBillList").set("parameter",entity);
view.get("#dsWayBillList").flushAsync(function(entityList){
	if (entityList.isEmpty()) {
		view.get("#msg").set("text","消息提示：查无资料！");
    }else {
		view.get("#msg").set("text","消息提示：查询成功！");
    }
});
chooseEntitys = {};
view.set("#dgWayBill.selection", []);

var dataGrid = view.get("#dgWayBill");
var selEntitys = dataGrid.get("selection");
if(selEntitys.length<1){
	view.get("#msg").set("text","消息提示：请至少勾选一条数据！");
	return false;
}
var ids="";
var i=0;
for(i;i<selEntitys.length;i++){
	if(i==0){
		ids=selEntitys[i].get("order_Id");
		
	}else{
		ids=ids+","+selEntitys[i].get("order_Id");
	}
}
var ajaxExcuteBefore = view.get("#ajaxCheckIfUpdate");
ajaxExcuteBefore.set("parameter",{ids:ids});
ajaxExcuteBefore.execute(function(returnValue){
    var flag = returnValue["flag"];
	if(flag){
		view.get("#msg").set("text","消息提示：勾选的数据中存在己对账数据，不能修改付款方式");
	    return false;
	}else{
		var action=view.get("#uaSaveUpdate");
        action.execute(function(returnValue){
		    view.get("#dsWayBillList").set("parameter",{});
			var entityAdd = view.get("#afCondition").get("entity");
		    view.get("#dsWayBillList").set("parameter",entityAdd);
			view.get("#dsWayBillList").flushAsync();
			view.get("#msg").set("text","消息提示： "+returnValue["msg"]);
        });
	}
});

var dataGrid = view.get("#dgWayBill");
var dtWayBillList=view.get("#dtWayBillList");

var selEntitys = new Array();
for(var key in chooseEntitys) {
	selEntitys.push(chooseEntitys[key]);
}

if(selEntitys.length<1){
	view.get("#msg").set("text","消息提示：请至少勾选一条数据！");
	return false;
}

var count=0;
var ids = new Array();

var dataGridB = view.get("#dgBranch");

selEntitys = selEntitys.sort(function(entity1,entity2){
	return entity1.get("isBranchable") == '否' ? -1 : 1;
});

for(var i=0;i<selEntitys.length;i++){
	if(selEntitys[i].get("isBranchable")=="否"){
		count++;
	}else{
		
		ids.push(selEntitys[i].get("order_Id"));
	}
	
	var entity=new dorado.Entity(null,"before",dtWayBillList);
	entity.set("order_Id",selEntitys[i].get("order_Id"));
	entity.set("order_number",selEntitys[i].get("order_number"));
	entity.set("title",selEntitys[i].get("title"));
	entity.set("tname",selEntitys[i].get("tname"));
	entity.set("isBranchable",selEntitys[i].get("isBranchable"));
	view.get("#dsWayBillListBranch").insert(entity);
	
}

view.get("#ids").set("text",ids.join(","));
view.get("#tipMsg").set("text","注：系统检测到你选择的运单中有     "+count+"  单不可拆单，点击“拆单确认”，系统将自动过滤掉不可拆单的运单。");
view.get("#dlBranch").show();

