DataTree.ClientEvent:
var dropdown = dorado.widget.DropDown.findDropDown(self);
var currentNode = self.get("currentNode");
var entity = currentNode.get("data");
var addressName = entity.get("addressName");
var level=currentNode.get("level");
var parentNode = currentNode.get("parent");
if(level==1){
	if(!currentNode.get("hasChild")){
	   dropdown.close(addressName);
	}
}else if(level==2){
	entity = parentNode.get("data");
	addressName = entity.get("addressName") + "" + addressName;
	
	dropdown.close(addressName);
}else{
	entity = parentNode.get("data");
	addressName = entity.get("addressName") + "" + addressName;
	parentNode = parentNode.get("parent");
	
	entity = parentNode.get("data");
	addressName = entity.get("addressName") + "" + addressName;
	dropdown.close(addressName);
} 

/**if(level==1||level==2){
	currentNode.set("expanded",true);
