Button.ClientEvent:
var entity=view.get("#dsWayBillList").getData("#");
if(entity.get("payType")==null||""==entity.get("payType")){
	dorado.MessageBox.alert("请选择付款方式");
	return false;
}
var action=view.get("#uaSaveUpdate");
action.execute(function(returnValue){
	view.get("#dlAddUpdate").close();
	view.get("#dsWayBillList").set("parameter",{});
	var entityAdd = view.get("#afCondition").get("entity");
    view.get("#dsWayBillList").set("parameter",entityAdd);
	view.get("#dsWayBillList").flushAsync();
	view.get("#msg").set("text","消息提示： "+returnValue["msg"]);
	
});

UpdateAction.ClientEvent:
var dataGrid = view.get("#dgWayBill");
var selEntitys = dataGrid.get("selection");
arg.data=selEntitys;

Button.ClientEvent:
var ids=view.get("#ids").get("text");
	
dorado.MessageBox.confirm("本次操作将无法撤销，是否确认操作？",function(){
	var aaBranch=view.get("#aaBranch");
	aaBranch.set("parameter",{
	       "ids":ids
    });
	aaBranch.execute(function(returnValue){
		dorado.MessageBox.alert(returnValue["msg"]);
		view.get("#dlBranch").close();
		view.get("#dsWayBillList").flush();
	});
});
