ToolBarButton.ClientEvent:
var dgSh10b = view.get("#dgSh10b");
var sh10Entity = view.get("#dsSh10").getData("#");
var transNo = sh10Entity.get("transNo");
var selection = dgSh10b.get('selection');//获取所有选中的数据

if(selection.length<1){
	view.get("#msgb").set("text","消息提示：请至少勾选一条数据！");
	return false;
}
selection.each(function(entity){
	entity.set("transNo",transNo);//运单
});
//sh10aEntity.set("backStatus","1");
var uaSh10b = view.get("#uaSh10b");
uaSh10b.execute(function(returnValue){
    var flag = returnValue["flag"];
    var msg = returnValue["msg"];
	if(flag){
		view.get("#msgb").set("text","消息提示： "+msg);
		var sh10Entity = view.get("#dsSh10").getData("#");
		var transNo = sh10Entity.get("transNo");
		//根据得到的数据集向其中插入一条对象
		with(view.get("#dsSh10b")){
			set("parameter", {"transNo":transNo});
			flushAsync();
		}
	    return false;
	}else{
		view.get("#msgb").set("text","消息提示： "+msg);
	}
});

