Button.ClientEvent:
var entityForm = view.get("#carrierForm").get("entity");
var data = view.get("#dsSh10a").getData("#");
var dgSh10a = view.get("#dgSh10a");
var selection = dgSh10a.get('selection');//获取所有选中的数据
alert();
if(selection.length>0){
	var ajaxActionCarrierSh10a = view.get("#ajaxActionCarrierSh10a");
	var apPayType = data.get("apPayType");
	var apUnitPrice = data.get("apUnitPrice");
	var apSettleType = data.get("apSettleType");
	var apPrePayments = data.get("apPrePayments"); 
	ajaxActionCarrierSh10a.set("parameter",{
		'carrierNo' : data.get("carrierNo"),
		'carrierName' : data.get("carrierName"),
		'apPayType' : data.get("apPayType"),
		'apUnitPrice' : data.get("apUnitPrice"),
		'apSettleType' : data.get("apSettleType"),
		'apPrePayments' : data.get("apPrePayments"),
		'ListSh10a' : selection
	});
	ajaxActionCarrierSh10a.execute(function(returnValue) {
		var flag = returnValue["flag"];
	    var msg = returnValue["msg"];
		if(flag){
			with (view.get("#dataSetTbih11")){
				set("parameter", data);
				view.get("#dataSetTbih11").flush();
			}
		}
	
	});	
}else{
	view.get("#msga").set("text","请先勾选需要批量设置的数据");
}
