DataPilot.ClientEvent:
var code = arg.code;
  switch(code){
    case "+":
    	//得到主表的数据集对象
    	var dsSh30 = view.get("#dsSh30");
    
    	//根据得到的数据集向其中插入一条对象，
    	dsSh30.insert();
    	view.get("#dlSh30").show();
    	//防止系统自动的添加一行
    	arg.processDefault = false;
    	break;
  }

ToolBarButton.ClientEvent:
var dsSh32 = view.get("#dsSh32");

//根据得到的数据集向其中插入一条对象，
dsSh32.insert();
view.get("#dlSh10").show();

var dsSh32 = view.get("#dsSh32");

//根据得到的数据集向其中插入一条对象，
dsSh32.insert();
view.get("#dlSh10").show();

var sh32Entity = view.get("#dsSh32").getData("#");
sh32Entity.set("status","1");
var uaSh32Confirm = view.get("#uaSh32Confirm"); 
uaSh32Confirm.execute(function(returnValue){
    var flag = returnValue["flag"];
    var msg = returnValue["msg"];
	if(flag){
		view.get("#msg").set("text","消息提示： "+msg);
	}else{
		view.get("#msg").set("text","消息提示： "+msg);
	}
});

var sh32Entity = view.get("#dsSh32").getData("#");
sh32Entity.set("status","0");
var uaSh32Confirm = view.get("#uaSh32Confirm"); 
uaSh32Confirm.execute(function(returnValue){
    var flag = returnValue["flag"];
    var msg = returnValue["msg"];
	if(flag){
		view.get("#msga").set("text","消息提示： "+msg);
	}else{
		view.get("#msga").set("text","消息提示： "+msg);
	}
});

DataPilot.ClientEvent:
 var code = arg.code;
   switch(code){
     case "-":
	 //得到从表的数据集对象
