	 var uaSh32aDel = view.get("#uaSh32aDel");
	 sh32aEntity.set("custNo","");
	 uaSh32aDel.execute(function(returnValue){
		view.get("#msg").set("text","消息提示： "+returnValue["msg"]);
	 });
   }

ToolBarButton.ClientEvent:
var dgSh30a = view.get("#dsSh30a");
var uaSh30a = view.get("#uaSh30a");
var selection = dgSh30a.get('selection');//获取所有选中的数据
selection.each(function(entity){
	entity.set("status","0");
});
uaSh30a.execute();

