ToolBarButton.ClientEvent:
var selection = view.get("#dgSh10N").get('selection');//获取所有选中的数据
if(selection.length<1){
	view.get("#msgaN").set("text","消息提示：请至少勾选一条数据！");
	return false;
}

selection.each(function(entity){ 
	entity.set("status","1");//结算确认
});
var uaSh10 = view.get("#uaSh10"); 
uaSh10.execute(function(returnValue){
    var flag = returnValue["flag"];
    var msg = returnValue["msgaN"];
	if(flag){
		view.get("#msgaN").set("text","消息提示： "+msg);
		with(view.get("#dsSh10")){
			set("parameter", data);
			flushAsync();
		}
	}else{
		view.get("#msgaN").set("text","消息提示： "+msg);
	}
});
