ToolBarButton.ClientEvent:
var dgSh10a = view.get("#dgSh10aN");
var selection = dgSh10a.get('selection');//获取所有选中的数据
if(selection.length<1){
	view.get("#msgaN").set("text","消息提示：请至少勾选一条数据！");
	return false;
}
var sh22Entity = view.get("#dsSh22").getData("#");
var shipListNo = sh22Entity.get("shipListNo");
var hc = sh22Entity.get("hc");
selection.each(function(entity){
	entity.set("shipListNo",shipListNo);//水单号
	//view.get("#dsSh22a").insert(entity.toJSON());
});
//sh10aEntity.set("backStatus","1");
var uaSh10a = view.get("#uaSh10a");
uaSh10a.execute(function(returnValue){
    var flag = returnValue["flag"];
    var msg = returnValue["msg"];
	if(flag){
		view.get("#msgaN").set("text","消息提示： "+msg);
		var transNo = sh22Entity.get("transNo");
		with(view.get("#dsSh10a")){
			set("parameter", {"transNo":transNo});
			flushAsync();
		}
	    return false;
	}else{
		view.get("#msgaN").set("text","消息提示： "+msg);
	}
});

DataGrid.ClientEvent:
var sh10Entity = view.get("#dsSh10").getData("#");
var sh22Entity = view.get("#dsSh22").getData("#");
var transNo = sh10Entity.get("transNo");
var custName = sh10Entity.get("custName");
sh22Entity.set("transNo",transNo);
sh22Entity.set("custName",custName);
sh22Entity.set("custNo",custNo);
view.get("#dlSh10").hide();

