ToolBarButton.ClientEvent:
var tmp =  new Array();
var i =0;
var boo ;
var selection = view.get("#dgSh10aN").get('selection');//获取所有选中的数据
if(selection.length<2){
	view.get("#msgaN").set("text","消息提示：请至少勾选两条数据！");
	return false;
}

selection.each(function(entity){
	var carrierNo = entity.get("carrierNo");
	if(i==0){
		tmp.push(carrierNo);
	}else{
		if(carrierNo!=""&&tmp!=carrierNo){
			boo= false;
		}
	}
	i=i+1;
	entity.set("status","11");//结算确认
});
if(boo==false){
	view.get("#msgaN").set("text","消息提示：供应商不同，不能合并结算");
	return false;
}else{
	var uaSh10a = view.get("#uaSh10a");
	uaSh10a.execute(function(returnValue){
		var flag = returnValue["flag"];
		var msg = returnValue["msg"];
		if(flag){
			view.get("#msgaN").set("text","消息提示： "+msg);	
		}else{
			view.get("#msgaN").set("text","消息提示： "+msg);
		}
	});
}

var sh10aEntity = view.get("#dsSh10a").getData("#");
sh10aEntity.set("status","1");
var uaSh10a = view.get("#uaSh10a");
uaSh10a.execute(function(returnValue){
