DataSetDropDown.ClientEvent:
var entity = view.get("#dsStartStation").getData("#");
var stationCode = entity.get("stationcode");
if(stationCode){
	dsStartLine.set("parameter",stationCode);
	dsStartLine.flushAsync();
}

var entity = view.get("#dsEndStation").getData("#");
var stationCode = entity.get("stationcode");
if(stationCode){
	dsEndLine.set("parameter",stationCode);
	dsEndLine.flushAsync();
}

CustomDropDown.ClientEvent:
var data=view.id("dsSh10a").getData("#");
if(!data){
	dorado.MessageBox.alert("请先选中一个角色!");
	arg.processDefault=false;
	return;
}
var dialog=view.id("$dialogCont");
	dialog.$show({
		width:500,
		height:500,
		callback:function(selectArray){
			dialog.hide();
			data.set("contNo",selectArray["contNo"]);
			data.set("prodType",selectArray["prodType"]);
			data.set("prodTypeName",selectArray["prodTypeName"]);
			data.set("productNo",selectArray["productNo"]);
			data.set("productName",selectArray["productName"]);
			data.set("prodDetail",selectArray["prodDetail"]);
			data.set("despatchNo",selectArray["despatchNo"]);
			data.set("despatchName",selectArray["despatchName"]);
			data.set("despatchAddress",selectArray["despatchAddress"]);
			data.set("despatchMobile",selectArray["despatchMobile"]);
		}
	});

