DataPilot.ClientEvent:
 var code = arg.code;
   switch(code){
     case "-":
	 //得到从表的数据集对象
	 var sh35Entity = view.get("#dsSh35").getData("#");
	 var uaSh35Del = view.get("#uaSh35Del");
	 sh35Entity.set("updateEmp","");
	 uaSh35Del.execute(function(returnValue){
		view.get("#msg").set("text","消息提示： "+returnValue["msg"]);
	 });
   }

ToolBarButton.ClientEvent:
var dsSh35 = view.get("#dsSh35");
var sh35Entity = dsSh35.getData("#");
var settleNo = sh35Entity.get("settleNo");
var status = sh35Entity.get("status");
if(settleNo!=null||settleNo!=""){
	if(status=="12"){
		view.get("#msg").set("text","消息提示：已结算确认，不能挑选船运单！");
		return false;
	}else{
		view.get("#dlSh35").show();
	}
}else{
	//根据得到的数据集向其中插入一条对象，
	dsSh35.insert();
	view.get("#dlSh35").show();
}

var dsSh35 = view.get("#dsSh35");

//根据得到的数据集向其中插入一条对象，
dsSh35.insert();
view.get("#dlSh35").show();

var sh35Entity = view.get("#dsSh35").getData("#");
sh35Entity.set("status","1");
var uaSh35Confirm = view.get("#uaSh35Confirm"); 
uaSh35Confirm.execute(function(returnValue){
    var flag = returnValue["flag"];
    var msg = returnValue["msg"];
	if(flag){
		view.get("#msg").set("text","消息提示： "+msg);
	}else{
		view.get("#msg").set("text","消息提示： "+msg);
	}
});

var sh35Entity = view.get("#dsSh35").getData("#");
sh35Entity.set("status","0");
var uaSh35Confirm = view.get("#uaSh35Confirm"); 
uaSh35Confirm.execute(function(returnValue){
    var flag = returnValue["flag"];
    var msg = returnValue["msg"];
	if(flag){
		view.get("#msga").set("text","消息提示： "+msg);
	}else{
		view.get("#msga").set("text","消息提示： "+msg);
	}
});

