ToolBarButton.ClientEvent:
var sh35Entity = view.get("#dsSh35").getData("#");
var settleNo = sh35Entity.get("settleNo");
var selection = view.get("#dgSh22").get('selection');//获取所有选中的数据
if(selection.length<1){
	view.get("#msgaN").set("text","消息提示：请至少勾选一条数据！");
	return false;
}

selection.each(function(entity){ 
	entity.set("status","1");//结算确认
	entity.set("settleNo",settleNo);//结算确认
});
var uaSh22 = view.get("#uaSh22"); 
uaSh22.execute(function(returnValue){
    var flag = returnValue["flag"];
    var msg = returnValue["msgaN"];
	if(flag){
		view.get("#msgaN").set("text","消息提示： "+msg);
		with(view.get("#dsSh22")){
			set("parameter", null);
			flushAsync();
		}
	}else{
		view.get("#msgaN").set("text","消息提示： "+msg);
	}
});

